#mw-content-text p{margin:1em 0}blockquote{border-left:2px solid #2f6fab;padding-left:10px}table.padding-5 td{padding:5px !important}table.padding-5 th{padding:5px !important}div.hint{border:1px dashed #2f6fab;background-color:#f9f9f9;color:#f9f9f9}span.code{font-family:Consolas,monospace;font-size:13px}span.help{text-decoration:none;border-bottom:1px dashed #2f6fab}span.node{border:1px solid #2f6fab;background-color:#f9f9f9;padding:0 3px}span.plot{font-family:Consolas,monospace;font-size:14px;font-weight:bold}.template-documentation{clear:both;margin:1em 0 0 0;border:1px solid #aaa;background-color:#ecfcf4;padding:1em}.messagebox{border:thin solid #aaa;margin:.5em 10%;background-color:#f9f9f9}table.messagebox td,table.messagebox th{border:none}td.mb-image{white-space:nowrap}td.mb-text{width:100%}.mb-warning,.mb-warning-orange{border:medium solid orange}.mb-warning-red{border:medium solid red}.mb-info,.mb-question{border:thin solid DodgerBlue}.mb-error{border:medium solid red}.mb-copyright{border:thin solid red}.mb-copyleft{border:thin solid green}.mb-stop{border:medium dashed red}#fileinfotpl td .messagebox,.messagebox.fullwidth,.mb-fullwidth{margin-left:0;margin-right:0}.mb-autowidth{width:auto;margin-left:auto;margin-right:auto}.mb-autowidth .mb-text{width:auto}.mb-nomargin{margin-left:0;margin-right:0}.mb-delete .messagebox{color:white}.mb-centered .mb-text{text-align:center}.messagebox.m0{background-color:#7535CB}.messagebox.m1{background-color:#65CF31}.messagebox.m2{background-color:#CBCF00}.messagebox.m3{background-color:#bb0000}.messagebox.mEd{background-color:#d0e0e0}.messagebox .messagebox{background-color:transparent;font-size:smaller;border-bottom:none;border-left:none;border-right:none;border-width:thin;border-color:#aaaaaa;margin-bottom:0;margin-left:0;margin-right:0;width:100%}.messagebox .messagebox .mb-image img{width:24px !important;height:auto !important}.hatnote{font-style:italic;padding-left:2em;margin-bottom:0.5em}.hatnote i{font-style:normal}div.mw-geshi div,pre{padding:1em;margin:1em 0;border:1px dashed #2f6fab;background-color:#f9f9f9;font-family:Consolas,monospace !important;font-size:13px }